mapper.select:
select cart_code, member_code, cart_amount, cart_size, cart_color, cart_taste, c.item_code, 
item_category, item_name, item_info, item_price, item_image, i.item_size, i.item_color, i.item_taste
from cart c, item i where c.item_code = i.item_code and member_code = #{member_code}

mapper.update:
update cart set

	cart_size=#{value}


	cart_color=#{value}


	cart_taste=#{value}

where cart_code = #{cartCode}
